Console.WriteLine(System.Text.RegularExpressions.Regex.Matches(File.ReadAllText("input.txt"), "(?=([a-z]{" + (Environment.GetEnvironmentVariable("part") == "part1" ? 4 : 14) + "}))").ToList().FindIndex((m) => m.Groups[1].Value.ToHashSet().Count == m.Groups[1].Value.Length) + (Environment.GetEnvironmentVariable("part") == "part1" ? 4 : 14));